import React from 'react'
import {makeStyles, withStyles} from "@material-ui/core/styles";
import {TextField, Typography, Button, Grid, Box, TextareaAutosize} from "@material-ui/core"
import SendIcon from "@material-ui/icons/Send"
import Navbar from './Navbar';
import clsx from "clsx";
import Typed from 'react-typed';

const useStyles = makeStyles(theme=>({
    form: {
        top: "50%",
        left:"50%",
        transform: "translate(-50%, -50%)",
        position: "absolute"
    },
    button: {
        marginTop: "1rem",
        color: "#4682B4",
        borderColor: "#4682B4",
        "&:hover": {
            backgroundColor: "white",
            borderColor: "white",
          },
    },
    largeInput: {
        "& .MuiOutlinedInput-input": {
          height: "150px", // Adjust the height as desired
          padding: "10px", // Add padding for better appearance
        },
    }

}));

const InputField = withStyles({
    root: {
        "& label.Mui-focused":
        {
        color: "#4682B4",
        },
        "& label": {
            color: "gray"
        },
        "& .MuiOutlinedInput-root": {
            "& fieldset": {
                borderColor: "gray"
            },
            "&:hover fieldset": {
                borderColor: "white"
            },
            "&.Mui-focused fieldset": {
                borderColor: "white"
            }
        }


    },
})(TextField);

const ParagraphInput = withStyles(theme => ({
    root: {
      "& label.Mui-focused": {
        color: "#4682B4",
      },
      "& label": {
        color: "gray",
      },
      "& .MuiOutlinedInput-root": {
        "& fieldset": {
          borderColor: "gray",
        },
        "&:hover fieldset": {
          borderColor: "white",
        },
        "&.Mui-focused fieldset": {
          borderColor: "white",
        },
      },
      "& .MuiInputBase-input": {
        padding: theme.spacing(1), // Adjust the padding as needed
        lineHeight: 1.5, // Adjust the line height as needed
      },
    },
  }))(TextareaAutosize);


const Contacts = () => {
    const classes = useStyles();
  return (
    <>
    <Navbar/>
    <Box component="div">
        <Grid container justify="center">
            <Box component= "form" className = {classes.form}>
                <Typography variant="h5" style={{color: "white", textAlign: "center", textTransform: "uppercase"}}>
                <Typed strings= {["Contact Me"]} typeSpeed={40} />
                </Typography>
                <br />
                <InputField fullWidth ={true}label ="Name..." variant="outlined" inputProps={{style:{color: "#4682B4"}}} margin = "dense" size = "medium"/>
                <br />
                <InputField fullWidth ={true}label ="Email..." variant="outlined"inputProps={{style:{color: "#4682B4"}}}margin = "dense" size = "medium"/>
                <br />
                <InputField fullWidth ={true}label ="Organization..." variant="outlined"inputProps={{style:{color: "#4682B4"}}} margin = "dense" size = "medium"/>
                <br />
                <ParagraphInput
  rowsMin={4} // Adjust the number of rows as desired
  placeholder="Message..."
  className={clsx(classes.largeInput, classes.inputField)}
/>

                <br />
                <Button variant="outlined" className={classes.button}fullWidth={true} endIcon={<SendIcon />}>
                    contact me
                </Button>
            </Box>
        </Grid>
    </Box>
    </>
  )
};

export default Contacts
